declare module '*.mp3' {
	const value: AudioBuffer
	export default value
}

declare module '*.wav' {
	const value: AudioBuffer
	export default value
}

declare module '*.png' {
	const value: HTMLImageElement
	export default value
}

declare module '*.mp4' {
	const value: HTMLVideoElement
	export default value
}

declare module '*.mkv' {
	const value: HTMLVideoElement
	export default value
}
